import React, { useEffect, useState } from "react";
import SearchBar from "./searchBar";
import Stats from "./stats";
import axios from "axios";

function IpAddress() {
  const [IpAddress, setIpAddress] = useState("");
  const [location, setLocation] = useState("");
  const [TimeZone, setTimeZone] = useState("");
  const [isp, setIsp] = useState("");
  const [coordinates, setCoordinates] = useState({});

  const fetchLocations = (ipAddress = "") => {
    axios
      .get(
        `https://geo.ipify.org/api/v2/country,city?apiKey=at_Iy5MJVbCI3T5zZaLjQmboVVuSMAXQ&ipAddress=${ipAddress}`
      )
      .then((resp) => {
        console.log(resp.data);
        setIpAddress(resp.data.ip);
        setLocation(
          `${resp.data.location.country},${resp.data.location.city},${resp.data.location.postalCode} `
        );
        setTimeZone(resp.data.isp);
      })
      .catch((err) => console.log(err));
  };
  useEffect(() => {
    fetchLocations();
  }, []);
  return (
    <div className="flex flex-col h-screen relative">
      <SearchBar />
      <Stats />
    </div>
  );
}

export default IpAddress;
